mapper.select:
SELECT *
FROM codefolio.proj
ORDER BY proj_seq DESC;

SELECT *
FROM codefolio.proj
where user=#{userId}
ORDER BY proj_seq DESC;

mapper.update:
UPDATE codefolio.proj
SET preview=#{preview}
WHERE proj_seq=#{projSeq};

UPDATE codefolio.proj
SET view = view +1
WHERE proj_seq = #{projSeq}
