mapper.select:
SELECT proj_seq, title, user, thumbnail FROM codefolio.proj WHERE title LIKE CONCAT_WS('%',#{keyword},'%') OR content LIKE CONCAT_WS('%',#{keyword},'%') OR stack LIKE CONCAT_WS('%',#{keyword},'%')
ORDER BY proj_seq ASC
LIMIT #{criteria.amount} OFFSET #{criteria.startNum}

SELECT *
FROM codefolio.proj
where user=#{userId}
ORDER BY proj_seq DESC;

mapper.update:
UPDATE codefolio.proj
SET preview=#{preview}
WHERE proj_seq=#{projSeq};

UPDATE codefolio.proj
SET view = view +1
WHERE proj_seq = #{projSeq}
